/* ----- navigation ----- */


.vbox-close {
  position: fixed;
  right: 32px;
  top: 32px;
  width: 32px;
  height: 32px;
  cursor:pointer;
  text-indent:-9999px;
}


.vbox-close:before, .vbox-close:after {
  position: absolute;
  left: 15px;
  content: ' ';
  height: 28px;
  width: 2px;
  background: #efd1be;
}

.vbox-close:before {
  transform: rotate(45deg);
  -webkit-transform: rotate(45deg);
  -moz-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
}

.vbox-close:after {
  transform: rotate(-45deg);
    transform: rotate(-45deg);
  -webkit-transform: rotate(-45deg);
  -moz-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
}



.vbox-next, .vbox-prev{
    box-sizing: content-box;
    cursor: pointer;
    position: fixed;
    top: 50%;
    color: #fff;
    width: 30px;
    height: 170px;
    margin-top: -85px;
    text-indent: -100px;
    border: solid transparent; /* Using border instead of padding to keep bg image in place */
    overflow: hidden;
}
.vbox-prev{
    left: 0;
    border-width: 0 30px 0 10px;
}
.vbox-next{
    right: 0;
    border-width: 0 10px 0 30px;
}

.vbox-title{
    width: 100%;
    height: 40px;
    float: left;
    text-align: center;
    line-height: 28px;
    font-size: 13px;
    color: #999999;
    padding: 6px 40px;
    overflow: hidden;
    position: fixed;
    display: none;
    top: 10px;
    left: 0;
	font-family: 'montserratregular';
	text-transform:uppercase;
	letter-spacing: 1px;

}

.vbox-title span{
	color:#181818;
}

.vbox-num{
    cursor: pointer;
    position: fixed;
    top: 10px;
    left: 10px;
    height: 40px;
    display: block;
    color: #181818;
    overflow: hidden;
    line-height: 28px;
    font-size: 16px;
    padding: 6px 10px;
    display: none;
	font-family: 'montserratregular';
}
/* ------- inline window ------ */
.vbox-inline{
    width: 420px;
    height: 315px;
    padding: 10px;
    background: #fff;
    text-align: left;
    margin: 0 auto;
    overflow: auto;
}

/* ------- Video & iFrames window ------ */
.venoframe{
    border: none;
    width: 960px;
    height: 720px;
}
@media (max-width: 992px) {
    .venoframe{
        width: 640px;
        height: 480px;
    }
}
@media (max-width: 767px) {
    .venoframe{
        width: 420px;
        height: 315px;
    }
}
@media (max-width: 460px) {
    .vbox-inline{
        width: 100%;
    }

    .venoframe{
        width: 100%;
        height: 260px;
    }
}

