/*Registry section*/

.block-registry {
    background: #ffffff;
    padding: 50px 40px;
}
.registry-form,
.select-input {
    position: relative;
}
.select-input:after {
    color: #b3b3b3;
    font-size: 18px;
    right: 0;
    position: absolute;
    top: 0;
