    -ms-appearance: none;
    cursor: pointer;
    margin-bottom: 0;
}
.registry-form .submit {
    background: #8de9be;
    cursor: pointer;
    position: relative;
    width: 100%;
    color: #ffffff;
    border: none;
    height: 42px;
    line-height: 0;
}
.success,
.error {
    display: none;
    position: relative;
}
.success h2,
.error h2 {
    font-size: 11px;
    position: absolute;
    right: 0;
    text-transform: none;
    top: -50px;
